// page init
jQuery(function(){
	initCarousel();
});

// scroll gallery init
function initCarousel() {
	jQuery('div.gallery').scrollGallery({
		mask: 'div.mask',
		slider: 'ul.slides',
		slides: '> li',
		btnPrev: 'a.btn-prev',
		btnNext: 'a.btn-next',
		generatePagination: '.pagination',
		autoRotation: true,
		switchTime: 5000,
		animSpeed: 500
	});
}

